function Rating(props) {
  // const { rating, numReviews } = props;
  const { rating, numReviews, caption } = props;

  const ratingNumber = parseFloat(rating.split('-'));

  return (
    <div className="rating">
      <span>
        <i
          className={
            ratingNumber >= 1
              ? 'fas fa-star'
              : ratingNumber >= 0.5
              ? 'fas fa-star-half-alt'
              : 'far fa-star'
          }
        />
      </span>
      <span>
        <i
          className={
            ratingNumber >= 2
              ? 'fas fa-star'
              : ratingNumber >= 1.5
              ? 'fas fa-star-half-alt'
              : 'far fa-star'
          }
        />
      </span>
      <span>
        <i
          className={
            ratingNumber >= 3
              ? 'fas fa-star'
              : ratingNumber >= 2.5
              ? 'fas fa-star-half-alt'
              : 'far fa-star'
          }
        />
      </span>
      <span>
        <i
          className={
            ratingNumber >= 4
              ? 'fas fa-star'
              : rating >= 3.5
              ? 'fas fa-star-half-alt'
              : 'far fa-star'
          }
        />
      </span>
      <span>
        <i
          className={
            ratingNumber >= 5
              ? 'fas fa-star'
              : ratingNumber >= 4.5
              ? 'fas fa-star-half-alt'
              : 'far fa-star'
          }
        />
      </span>
      {/* <span> {numReviews} reviews</span> */}
      {caption ? (
        <span>{caption}</span>
      ) : (
        <span> {' ' + numReviews + ' reviews'}</span>
      )}
    </div>
  );
}
export default Rating;
